    public static int[] sorter(int[] x){
        int f = 0;
        int l = 0;
        for(int i = 0; i < x.length; i++){
            for(int b = 0; b < x.length-1;b++){
                f = x[b];
                l = x[b+1];
                
                if(f > l){
                    x[b] = l;
                    x[b+1] = f;
                    swapcount += 1;
                }
            }
            compcount += 1;
        }
        return x;
    }

